class ClassCom extends Component {
	constructor() {
		super()

		this.state = {
			num: 100,
			title: '这是标题'
		}

		this.handleReduce = () => {
			console.log(this)
			this.setState({
				num: this.state.num - 1
			})
		}

		this.handleAdd = () => {
			this.setState(prevState => {
				console.log(prevState)
				return {
					num: prevState.num + 1
				}
			}, (currentState) => {
				console.log(currentState.num)
			})
		}
	}

	render() {
		let { name } = this.props
		let { num, title } = this.state
		console.log(this.props)
		return (
			<div>
				<h1 key={ 1 } title={ title } onClick={ () => { this.setState({ title: title + '-1' }) } }>hello { name } { title }</h1>
				<div className="calcWra">
					<button className="reduce" onClick={ this.handleReduce }>-</button>
					<span>{ num }</span>
					<button className="add" onClick={ this.handleAdd }>+</button>
				</div>
				{
					[1,2, 3, 4].map(v => <p key={ v+ '123' }>{ v }</p>)
				}

				{/*{ this.props.children }*/}
			</div>

		)
	}
}



